#!/usr/bin/env python3

import re

dna=""

for found in re.finditer(r"(.{50})TATTAT(.{25})"  , dna):
  whole    = found.group(0)
  up       = found.group(1)
  down     = found.group(2)
  up_start = found.start(1) + 1
  up_end   = found.end(1)   + 1
  dn_start = found.start(2) + 1
  dn_end   = found.end(2)   + 1
  
  output = [ whole , up , str(up_start), str(up_end) , down , str(dn_start) , str(dn_end)  ]
  
  print( "\t".join(output) ) 
